mapper.insert:
INSERT INTO MEMBER
(
    NO
  , ID
  , PASSWD 
  , NAME 
  , EMAIL
) 
VALUES
(
    SEQ_MEMBER.NEXTVAL
  , #{id} 
  , #{passwd}
  , #{name}
  , #{email}
)

mapper.update:
UPDATE MEMBER
SET    PASSWD = #{passwd}
WHERE  ID     = #{id}

mapper.select:

SELECT * 
FROM   MEMBER
WHERE  ID = #{id} AND PASSWD = #{passwd}


SELECT * 
FROM   MEMBER
WHERE  PASSWD = #{passwd} AND  ID = #{id}

mapper.insert:
INSERT INTO BOARD
(
    NO 
  , TYPE
  , SUBJECT
  , BOARD_IMG
  , WRITER
  , CONTENT
  , SEARCHNUM
) 
VALUES
(
    seq_board.NEXTVAL
  , #{type} 
  , #{subject}
  , #{board_img}
  , #{writer}
  , #{content}
  , #{searchNum}
)

mapper.update:
UPDATE BOARD
SET    SEARCHNUM = (SEARCHNUM + 1)
WHERE  NO        = #{no}

UPDATE BOARD
SET     TYPE        = #{type}
	  , SUBJECT     = #{subject}
	  , BOARD_IMG   = #{board_img}
	  , CONTENT     = #{content}
	  , CHANGE_DATE = sysdate
WHERE   NO          = #{no}
